mapper.select:
SELECT `id`, `name`, `parent_id`, `chinese_name`, `english_name`, `status`, `type`, `url`, `icon`, `sort`, `update_time`, `create_time`, `user_id`
FROM (



            SELECT `id`, `name`, `parent_id`, `chinese_name`, `english_name`, `status`, `type`, `url`, `icon`, `sort`, `update_time`, `create_time`, um.user_id
            FROM sys_menu m
            LEFT JOIN sys_user_menu um on um.menu_id = m.id
            WHERE um.user_id = #{dto.userId}

                and m.status = #{dto.status}


                and m.type in

                    #{type}



                and m.name = #{dto.name}

            UNION ALL

        SELECT `id`, `name`, `parent_id`, `chinese_name`, `english_name`, `status`, `type`, `url`, `icon`, `sort`, `update_time`, `create_time`, null `user_id`
        FROM sys_menu m


                and m.status = #{dto.status}


                and m.type in

                    #{type}



                and m.name = #{dto.name}




        SELECT `id`, `name`, `parent_id`, `chinese_name`, `english_name`, `status`, `type`, `url`, `icon`, `sort`, `update_time`, `create_time`, um.user_id
        FROM sys_menu m
        LEFT JOIN sys_user_menu um on um.menu_id = m.id
        WHERE um.user_id = #{dto.userId}

            and m.status = #{dto.status}


            and m.type in

                #{type}



            and m.name = #{dto.name}



) t GROUP BY id
